string gWinMessage = "Wow, you're the big winner!";

default
{
    on_rez(integer start_param)
    {
        if (start_param == 0) { // it rezzed from inventory
            llSleep(1.0);
            vector xyz_angles = <180,0.0,0>; // This is to define a 1 degree change
            vector angles_in_radians = xyz_angles*DEG_TO_RAD; // Change to Radians
            rotation xyzq = llEuler2Rot(angles_in_radians); // Change to a Rotation
            llSetRot(llGetRot()* xyzq); //Do the Rotation...
        } else {
            llSleep(1.0);
            llSetPrimitiveParams([PRIM_TEMP_ON_REZ, TRUE]);
            llSetTimerEvent(6.0);
        }
    }

    timer()
    {
        llDie();
    }
}
