string          pEmail;
integer         pTotal_number           = 0;
integer         i                       = 0;
integer         pCurrentSlide           = 0;
integer         pSlideCount             = 0;
integer         dialog_channel2;
integer         reset_channel;
integer         pActiveQuestion         = 0;
integer         gSuccessInt             = 0;
integer         gMinPerc                = 0;
integer         gQueryCount             = 0;
integer         g_iLine;
float           gScore;
list            types                  = [INVENTORY_OBJECT,INVENTORY_TEXTURE];
list            pAccessList            = [];
list            pAwardsList            = [];
list            gQueryList             = [];
list            gAnswerList            = [];
list            gOneCard;
list            g_lTempLines;
key             pLastId;
key             pActiveId;
key             g_kQuery;
string          gMessage;
string          gSuccess                = "";
string          gFailure                = "";
string          tQuestion;
string          gCorrectAnswer;
string          gsCardOneName           = "exam_config";
string          g_sNoteCardName;
string          qName;
string          url = "http://www.p0qp0q.com/cgibin/mcfeeny/mcfeenySimple.php";
key             pOwner;
string          put_id;


list ListItemDelete(list mylist,string element_old) {
    integer placeinlist     = llListFindList(mylist, [element_old]);
    if (placeinlist != -1)
        return llDeleteSubList(mylist, placeinlist, placeinlist);
    return mylist;
}

// This puts the data to the database (SAVE)
PutData(string boo2, string who, string msg)
{
    string args;
    args += "?key=";
    boo2 = llEscapeURL(boo2);
    args += boo2;
    args +="&email="+who+"&message=";
    msg = llEscapeURL(msg);
    args += msg;
    args += "&secret=M4g1C14N";
    put_id = llHTTPRequest(url+args,[HTTP_METHOD,"GET",HTTP_MIMETYPE,"application/x-www-form-urlencoded"],"");
}

getNextQuestion()
{
    //llSay(0, "42");
    string tLineText        = llList2String(gOneCard,(pActiveQuestion+7));
    string comm             = left(tLineText, ":");
    string tQA              = right(tLineText, ":");
    tQuestion               = left(tQA, ":");
    tQA                     = right(tQA, ":"); // all the answers
    list tAnswerList        = [];
    if (tQA != "")
    {
        integer tAnswerIter;
        for (tAnswerIter = 0; tAnswerIter< 5; ++tAnswerIter)
        {
            string tA1                  = left(tQA, ":");
            string tSplat               = llGetSubString(tA1, 0, 0);
            if (tSplat == "*")
            {
                integer tMP             = llStringLength(tA1);
                gCorrectAnswer          = llGetSubString(tA1, 1, (tMP-1));
                tA1                     = gCorrectAnswer;
            }
            if (tA1 != "")
            {
                tAnswerList             += [tA1];
            }
            tQA                         = right(tQA, ":");
        }
        llDialog( pActiveId, ("\n"+tQuestion), tAnswerList, dialog_channel2);
    }
}


string left(string src, string divider) {
    integer index                       = llSubStringIndex( src, divider );
    if(~index)
    {
        return llDeleteSubString( src, index + llStringLength(divider)-1, -1);
    }
    return src;
}

string right(string src, string divider) {
    integer index                       = llSubStringIndex( src, divider );
    string tString                      = "";
    if (~index)
    {
        return llDeleteSubString( src, 0, index + llStringLength(divider) - 1);
    }
    return tString;
}

initialize(string _action) {
    if (_action == "") {
        loadNoteCard(gsCardOneName);
    } else if (_action == "finish") {
        gQueryCount                 = ((llGetListLength(gOneCard)) - 7);
        integer i;
        for (i = 0; i< 7; ++i)
        {
            string tLineText        = llList2String(gOneCard,i);
            if (i == 0)
            {
                gMessage            = right(tLineText,":");
            }
            if (i == 1)
            {
                gSuccess            = right(tLineText,":");
                gSuccessInt         = (integer)gSuccess;
            }
            if (i == 2)
            {
                gFailure            = right(tLineText,":");
            }
            if (i == 3)
            {
                gMinPerc            = ((integer)(right(tLineText,":")));
            }
            if (i == 4)
            {
                pEmail              = right(tLineText,":");    
            }
            if (i == 5)
            {
                dialog_channel2     = ((integer)(right(tLineText,":")));
                //llSay(0, (string)dialog_channel2);
            }
            if (i == 6)
            {
                reset_channel       = ((integer)(right(tLineText,":")));   
            }
                llListen(dialog_channel2,"", "","");
                llListen(reset_channel, "", "", ""); // RESET channel

        }
    }
}

loadNoteCard( string _notecard ) {
    g_lTempLines                    = [];
    g_sNoteCardName                 = _notecard;
    g_iLine                         = 0;
    g_kQuery                        = llGetNotecardLine(g_sNoteCardName, g_iLine);

}

notecardFinished(string _notecard){
    if (_notecard == gsCardOneName) {
        gOneCard                 = g_lTempLines;
        initialize("finish");
    }
}

default
{
    state_entry()
    {
        qName                   = llGetObjectName();
    }

    on_rez(integer start_param)
    {
        initialize("");
    }

    touch_start(integer total_number)
    {
        pTotal_number               = total_number;
        list insertNameList         = [(string)pLastId];
        list tTest                  = [(string)pLastId];
        integer foundIndex          = llListFindList(pAwardsList, tTest);
        pLastId = llDetectedKey(0);
        if (foundIndex != -1)
            {
                pActiveQuestion = 0;
                gScore          = 0;
                pActiveId = pLastId;
                llInstantMessage(llDetectedKey(0), "You have already earned this prize. You may play again, but will not be awarded another prize.");
                getNextQuestion();
            }else{

                pActiveId               = pLastId;
                pActiveQuestion         = 0;
                gScore                  = 0;
                llSay(0, gMessage);
                getNextQuestion();
            }
    }


    listen(integer cNum, string name, key id, string mes)
    {
        //llSay(0, (string)cNum + " " + name + " " + (string) id + " " + mes);
        if (cNum == dialog_channel2) // this is a dialog response
        {
            // add a parse for id here to verify that we got the message from the active player
            //llSay(0, mes);
            if (id == pActiveId)
            {
            // first parse for the correct answer
                string tttCorrectAnswer = gCorrectAnswer;
                if (tttCorrectAnswer == mes)
                    // if they got the answer right
                {
                    float tPoints             = (100.0/gQueryCount);
                    gScore                      = gScore + tPoints;
                    integer ttPoints = llRound(gScore);
                    llSay(0, "That's correct! You earned "+((string)tPoints) + ".  You now have "+ (string)ttPoints + " points.");
                // inform the player that they got the answer right
                }else{
                    llSay(0, "Sorry, that answer is incorrect.");
                    // note we could add optional corrective hinting here
                }
            }
            // this chunk advances us to the next sequential question
            ++pActiveQuestion;
            if (pActiveQuestion < gQueryCount)
            {
                getNextQuestion();
            }else{
                if (gScore >= gMinPerc)
                {
                    llSetTimerEvent(0.0);
                    list insertNameList = [(string)pLastId];
                    list tTest          = [(string)pLastId];
                    integer foundIndex = llListFindList(pAwardsList, tTest);
                    if (foundIndex == -1)
                    {
                        pAwardsList = llListInsertList(pAwardsList, insertNameList, 0);
                        llInstantMessage(pActiveId, gSuccess);
                        //llSay(1791, (string)pActiveId); - NOT USING HUD FOR QUIZZER
                        pActiveQuestion = 0;
                        vector myPos = llGetPos();
                        vector relativePosOffset = <0.0,0.0,0.5>;
                        rotation myRot = llGetRot();
                        rotation relativeRot = <0.0,0.0,0.0,0.0>;
                        vector rezPos = myPos+relativePosOffset*myRot;
                        rotation rezRot = relativeRot*myRot;
                        //-------------------------------------
                        integer i = 0;
                        for (i=0;i < pTotal_number;i++)
                        {
                            integer invCount = llGetListLength(types);
                            integer itemIter;
                            integer thisTypeIter;
                            integer type;
                            integer thisTypeCount;
                            string scriptName = llGetScriptName();
                            string itemName;
                            string tMessage = name + " completed QUIZOMATIC " + qName + " and earned " + (string)llRound(gScore) + " points.";

                            for (itemIter=0; itemIter<invCount;itemIter++)
                            {
                                type = llList2Integer(types,itemIter);
                                thisTypeCount = llGetInventoryNumber(type);
                                if (thisTypeCount > 0)
                                {
                                    for (thisTypeIter=0; thisTypeIter<thisTypeCount;thisTypeIter++)
                                    {
                                        itemName = llGetInventoryName(type,thisTypeIter);
                                        if (scriptName != itemName)
                                        {
                                            llGiveInventory(pActiveId,itemName);
                                            llRezObject(itemName, rezPos, <0.0,0.0,0.0>, rezRot, 1);
                                        }
                                    }
                                }
                            }

                            llInstantMessage(llGetOwner(),"SecondLife Quiz-O-Matic Score Report for "+ name +"\n"+tMessage); 
                            pOwner = llGetOwner();
                            string boo = (string)pOwner;
                            PutData(boo, pEmail, tMessage);
                            //llEmail(pEmail, "SecondLife Quiz-O-Matic Score Report for "+name, tMessage);
                        }
                        pAccessList = [];
                        pAwardsList = [];
                    }
                }else{
                    llSetTimerEvent(0.0);
                    llInstantMessage(pActiveId, gFailure);
                    pActiveQuestion         = 0;
                    gSuccessInt             = 0;
                    gScore                  = 0;
                    string userId           = ((string)pLastId);
                    pAccessList             = ListItemDelete(pAccessList, userId);
                    pAccessList             = [];
                    pAwardsList             = [];
                }
            }
        }else{
            if (cNum == reset_channel)
            {
            if (mes == "reset") 
            {
                llSay(0, "resetting user access list");
                pAccessList                 = [];
                pAwardsList                 = [];
                initialize("");
            }
        }
        }
    }
    dataserver(key _query_id, string _data)
    {
        if (_query_id == g_kQuery) {
            if (_data != EOF) {
                g_lTempLines += [_data];
                g_iLine++;
                g_kQuery = llGetNotecardLine(g_sNoteCardName, g_iLine);
            } else {
                    notecardFinished(g_sNoteCardName);
            }
        }
    }
}
