//----------------- PRIVATE -----------------------------
string left(string src, string divider) {
    integer index                       = llSubStringIndex( src, divider );
    if(~index)
    {
        return llDeleteSubString( src, index + llStringLength(divider)-1, -1);
    }
    return src;
}

string right(string src, string divider) {
    integer index                       = llSubStringIndex( src, divider );
    string tString                      = "";
    if (~index)
    {
        return llDeleteSubString( src, 0, index + llStringLength(divider) - 1);
    }
    return tString;
}

default
{
    state_entry()
    {
        llListen(3720, "", "", "");
    }

    listen(integer channel, string name, key id, string mes)
    {
        string tMyName = llGetObjectName();
        string tLeft = left(mes, ":");
        string tRight = right(mes, ":");

        if (llGetOwner() == (key)tLeft)
            { 
            //llSleep(1);
            integer tVeritas = 0;
            tVeritas = (tMyName == tRight);
            if (tVeritas == 1)
                {
                    llSay(3721, tLeft + ":" + (string)llGetAttached());
                }
            }
    }
}
